function flowNodeDropActionPublic(jsonNodeObject) {
	// set "businessId" into "newActivityObject"'s extlCode
	var newActivityData = processData.businessflowData.getNewActivityObject();
	newActivityData.extlCode = jsonNodeObject.id;
//	newActivityData.activityId = jsonNodeObject.id;
	// set "sidebarData" into "newActivityObject"'s activitySpecification
	var sidebarData = processData.getSidebarDataById(jsonNodeObject.businessId);
	newActivityData.activitySpecification = sidebarData;
	
	// set "newActivityData" into "processData"
	processData.businessflowData.putActivityDataAsNewOne(newActivityData);
}